
public class Page160_Exam2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		int total = 0;

		for (int i = 1; i <= 100; i++) {
			if (i % 3 == 0) {
				total += i;

			}

		}

		System.out.println("1~ 100까지 3의 배수 총합:" + total);

		
		
		
		
		
		
	}

}
